import React, { Component } from 'react'
import axios from 'axios'
import { POKEMON_API_URL } from '../config'
import { Box, CircularProgress, Typography, withStyles } from '@material-ui/core'


const styles = (theme) => ({
    pokedexContainer: {
        height: '80vh',
        backgroundColor: 'black'
    }
})

class PokemonDetails extends Component {
    constructor (props) {
        super(props)
        this.state = {
            pokemon: null
        }
    }
    componentDidMount () {
          const { match,styles } = this.props
          const { id } = match?.params
          axios.get(POKEMON_API_URL + "/" + id).then((response) => {
              if(response.status >= 200 && response.status < 300) {
                    this.setState({pokemon: response.data})
              } 
          })
    } 
    render() { 
        const { pokemon } = this.state
        if(pokemon){
            const { name } = pokemon
            return (
                <Box>
                    <Box className={styles.pokedexContainer}></Box>
                    <Typography className={styles.textTitle } variant='h1'></Typography>
                    {name}
                </Box>

            )
        } else{
            return <CircularProgress />
        }
    }
}

export default withStyles(styles)(PokemonDetails);
