import React, { Component } from 'react'
import axios from 'axios'
import { POKEMON_API_URL } from '../config'
import { Box, CircularProgress, Typography, withStyles } from '@material-ui/core'


const styles = (theme) => ({
    pokedexContainer: {
        height: '84vh',
        backgroundColor: 'black',
        color: 'white',
        marginTop: 75,
        textAlign: 'center',
        borderRadius: 5,
        paddingTop: 30
    },
    textTitle: {
        textTransform: "upperCase",
        fontFamily: "Fantasy"
    },
    pokemonImage: {
        width: "170px",
        height: "170px"
    }
})

class PokemonDetails extends Component {
    constructor (props) {
        super(props)
        this.state = {
            pokemon: null
        } 
    }
    componentDidMount () {
          const { match } = this.props
          const { id } = match?.params
          axios.get(POKEMON_API_URL + "/" + id).then((response) => {
              if(response.status >= 200 && response.status < 300) {
                    this.setState({pokemon: response.data})
              } 
          })
    } 
    render() { 
        const { classes } = this.props
        const { pokemon } = this.state;
        if(pokemon){
            const { name,sprites } = pokemon
            return (
                <Box>
                    <Box className={classes.pokedexContainer}>
                        <Typography className={classes.textTitle} variant='h1'>
                            {name}
                        </Typography>
                        <img className={classes.pokemonImage} src={sprites.font_default}/>
                    </Box>
                </Box>

            )
        } else{
            return <CircularProgress />
        }
    }
}

export default withStyles(styles)(PokemonDetails);
